import React, { useState } from "react";
import useFormValidation from "../../utils/useFormValidation.js";

export default function Login({ handleLogin }) {

  const { value, error, reset, handleChange } = useFormValidation();

  function handleSubmit(event) {
    event.preventDefault();
    handleLogin({
      email: value.email,
      password: value.password
    }, reset)
  }

  return (
    <div className="auth">
      <h2 className="auth__title">Вход</h2>
      <form 
        className="auth__form"
        onSubmit={handleSubmit}
        >
        <input 
          className={`popup__input ${isInputValid.email === undefined || isInputValid.email ? '' : 'popup__input_error'}`} 
          name="email"
          type="email" 
          placeholder="Email"
          value={email}
          onChange={handleChange}
          minLength="3"
          maxLength="40"
          required
          />
          <span className="popup__error">
          {error.email}
        </span>
        <input 
          className={`popup__input ${isInputValid.password === undefined || isInputValid.password ? '' : 'popup__input_error'}`}
          name="password"
          type="password" 
          placeholder="Пароль"
          minLength={3}
          value={password}
          onChange={handleChangePassword}
          required
          />
        <button className="auth__submit" type="submit">Войти</button>
      </form>
    </div>
  )
}




