import React, { useState } from "react";
import { Link } from "react-router-dom";
import useFormValidation from "../../utils/useFormValidation.js";

export default function Register({ handleRegister }) {
  const { value, error, isValid, isInputValid, reset } = useFormValidation();

  function handleSubmit(e) {
    e.preventDefault();
    handleRegister({
      email: value.email,
      password: value.password
    }, reset)
  }

  return (
    <div className="auth">
      <h2 className="auth__title">Регистрация</h2>
      <form className="auth__form" 
        onSubmit={handleSubmit} 
        // isValid={isValid}
        >
        <input
          // className="auth__input"
          className={`popup__input popup__input_text_name ${isInputValid.email === undefined || isInputValid.email ? '' : 'popup__input_error'}`}
          name="email"
          type="email"
          placeholder="Email"
          onChange={handleChange}
          value={registerData.email}
          minLength="3"
          maxLength="40"
          // isInputValid={isInputValid.email}
          // error={error.email}
          required
        />
        <input
          // className="auth__input"
          className={`popup__input popup__input_text_name ${isInputValid.password === undefined || isInputValid.password ? '' : 'popup__input_error'}`}
          type="password"
          name="password"
          placeholder="Пароль"
          minLength="5"
          maxLength="200"
          required
          onChange={handleChange}
          value={registerData.password}
          // error={error.password}
          // isInputValid={isInputValid.password}
        />
        <button className="auth__submit" type="submit" onSubmit={handleSubmit}>
          Зарегистрироваться
        </button>
      </form>
      <Link className="auth__link" to="/sign-in">
        Уже зарегистрированы? Войти
      </Link>
    </div>
  );
}





