import Header from "./Header/Header.jsx";
import Main from "./Main/Main.jsx";
import Footer from "./Footer/Footer.jsx";
import PopupWithForm from "./PopupWithForm/PopupWithForm.jsx";
import ImagePopup from "./ImagePopup/ImagePopup.jsx";
import { useCallback, useState } from "react";
import CurrentUserContext from "../contexts/CurrentUserContext.js";

function App() {

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false)
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false)
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false)
  const [selectedCard, setSelectedCard] = useState({})
  const [isImagePopup, setIsImagePopup] = useState(false)
  const [isDeleteCardPopupOpen, setIsDeleteCardPopupOpen] = useState(false)

  const [currentUser, setCurrentUser] = useState({})

  const setStatesCloseAllPopups = useCallback(() => {
    setIsEditAvatarPopupOpen(false)
    setIsEditProfilePopupOpen(false)
    setIsAddPlacePopupOpen(false)
    setIsImagePopup(false)
    setIsDeleteCardPopupOpen(false)
  },[])

  const closePopupOnEsc = useCallback((event) => {
    if (event.key === 'Escape') {
      setStatesCloseAllPopups()
      document.removeEventListener('keydown', closePopupOnEsc)
    }
  },[setStatesCloseAllPopups])

  const closeAllPopups = useCallback(() => {
    setStatesCloseAllPopups()
    document.removeEventListener('keydown', closePopupOnEsc)
  },[setStatesCloseAllPopups, closePopupOnEsc])

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
    setEvantListenersDocument()
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
    setEvantListenersDocument()
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
    setEvantListenersDocument()
  }

  function handleCardClick(card) {
    setSelectedCard(card)
    setIsImagePopup(true)
    setEvantListenersDocument()
  }

  function handleCardDelete() {
    setIsDeleteCardPopupOpen(true)
    setEvantListenersDocument()
  }

  function setEvantListenersDocument() {
    document.addEventListener('keydown', closePopupOnEsc)
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page__container">
        <Header/>

        <Main
        onEditProfile = {handleEditProfileClick}
        onAddPlace = {handleAddPlaceClick}
        onEditAvatar = {handleEditAvatarClick}
        onCardClick = {handleCardClick}
        onCardDelete = {handleCardDelete}
        />

        <Footer/>

        <PopupWithForm 
        name='edit-profile' 
        title='Редактировать профиль'
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        >
          <input
          id="name"
          name="name"
          type="text"
          placeholder="Имя"
          className="popup__input popup__input_text_name"
          required=""
          minLength={2}
          maxLength={40}
          />
          <span className="popup__error" id="name-error" />
          <input
          id="description"
          name="description"
          type="text"
          placeholder="О себе"
          className="popup__input popup__input_text_description"
          required=""
          minLength={2}
          maxLength={200}
          />
          <span className="popup__error" id="description-error" />
        </PopupWithForm>

      <PopupWithForm 
        name='add-card' 
        title='Новое место'
        button='Создать'
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
      >
        <input
          id="mesto"
          name="mesto"
          type="text"
          placeholder="Название"
          className="popup__input popup__input_name"
          required=""
          minLength={2}
          maxLength={30}
        />
        <span className="popup__error" id="mesto-error" />
        <input
          id="link"
          name="link"
          type="url"
          placeholder="Ссылка на картинку"
          className="popup__input popup__input_link"
          required=""
        />
        <span className="popup__error" id="link-error" />
      </PopupWithForm>

      <PopupWithForm 
        name='edit-avatar' 
        title='Обновить аватар'
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
      >
        <input
          className="popup__input popup__input_avatar"
          name="avatar"
          id="avatar"
          type="url"
          placeholder="ссылка на картинку"
          required=""
        />
        <span className="popup__error" id="avatar-error" />
      </PopupWithForm>

      <PopupWithForm 
        name='delete-card' 
        title='Вы уверены?'
        button='Да'
        isOpen={isDeleteCardPopupOpen}
        onClose={closeAllPopups}
      />

      <ImagePopup 
        card={selectedCard} 
        isOpen={isImagePopup}
        onClose={closeAllPopups}
      />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
