import React from "react";

export default function Login({ handleLogin }) {

  const { value, error, isValid, isInputValid, handleChange, reset, setValues } = useFormValidation();

  return (
    <div className="auth">
      <h2 className="auth__title">Вход</h2>
      <form className="auth__form">
        <input className="auth__input" type="text" placeholder="Email" />
        <input className="auth__input" type="password" placeholder="Пароль"/>
        <button className="auth__submit" type="submit">Войти</button>
      </form>
    </div>
  )
}
