import { useState } from 'react'
import { Route, Link } from 'react-router-dom'
import logo from '../../images/logo/Vector.svg'

export default function Header({ loggedIn, email }) {
  const [isSigningUp, setIsSigningUp] = useState(false);
  const handleAuthClick = () => {
    setIsSigningUp()
  }

  return (
    <header className="header">
      <img
      className="header__logo"
      src={logo}
      alt="логотип Mesto"
      />
      <div className="header__info">
        {loggedIn && <div className="header__email">{email}</div>} 
        {loggedIn ? (<Link className="header__auth" to="/sing-in" >Выйти</Link>) 
        : (
          <>
            <Link className="header__auth" to="/sign-in">Войти</Link>
            <Link className="header__auth" to="/sign-up">Регистрация</Link>
          </>
        )}
      </div>
    </header>
  )
}
