  //     })
  //     .catch((err) => console.error(`Ошибка при загрузке ${err}`));
  // }, []);


  useEffect(() => {
    setIsLoading(true);
    loggedIn && Promise.all([api.getInfo(), api.getInitialCards()])
      .then(([user, cards]) => {
        setCurrentUser(user);
        setCards(cards);
        setIsLoading(false);
      })
      .catch((err) => console.error(`Ошибка при загрузке ${err}`));
  }, [loggedIn]);

  // useEffect(() => {
  //   if (localStorage.jwt) {
