import { useContext, useEffect, useState } from "react";
import api from "../../utils/api";
import Card from "../Card/Card.jsx";
import CurrentUserContext from "../../contexts/CurrentUserContext";

export default function Main({
  onEditProfile,
  onAddPlace,
  onEditAvatar,
  onCardClick,
  onCardDelete
}) {
  const currentUser = useContext(CurrentUserContext)

  const [userName, setUserName] = useState("");
  const [userDescription, setUserDescription] = useState("");
  const [userAvatar, setUserAvatar] = useState("");
  const [cards, setCards] = useState([]);

  useEffect(() => {
    Promise.all([api.getInfo(), api.getInitialCards()]).then(
      ([user, cards]) => {
        setUserName(user.name);
        setUserDescription(user.about);
        setUserAvatar(user.avatar);
        cards.forEach((element) => (element.myid = user._id));
        setCards(cards);
      }
    );
  }, []);

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__container">
          <button
            type="button"
            className="profile__edit-avatar"
            onClick={onEditAvatar}
          >
            <img className="profile__avatar" src={userAvatar} alt="Аватарка" />
          </button>
          <div className="profile__info">
            <div className="profile__name-container">
              <h1 className="profile__name">{userName}</h1>
              <button
                className="profile__edit-button"
                type="button"
                onClick={onEditProfile}
              />
            </div>
            <p className="profile__description">{userDescription}</p>
          </div>
          <button
            className="profile__add-button"
            type="button"
            onClick={onAddPlace}
          />
        </div>
      </section>
      <section aria-label="Карточки с фотографиями" className="photos">
        <div className="cards">
          {cards.map((data) => {
            return (
              <Card card={data} key={data._id} onCardClick={onCardClick} onCardDelete={onCardDelete}/>
            );
          })}
        </div>
      </section>
    </main>
  );
}
